function getMidnightISODateInCurrentTimezone() {
  const now = new Date();

  // Get the year, month, and day in the current timezone
  const year = now.getFullYear();
  const month = now.getMonth();
  const day = now.getDate();

  // Create a new Date object representing midnight in the current timezone
  const midnight = new Date(year, month, day);

  // Convert to ISO string.  The important part here is using toISOString() which
  // handles the timezone conversion for you automatically.
  return midnight.toISOString();
}

