function getMidnightISODateSevenDaysAgo() {
  const now = new Date();

  // Calculate the date 7 days ago
  const sevenDaysAgo = new Date(now);
  sevenDaysAgo.setDate(now.getDate() - 7);

  // Extract year, month, and day from the date 7 days ago
  const year = sevenDaysAgo.getFullYear();
  const month = sevenDaysAgo.getMonth();
  const day = sevenDaysAgo.getDate();

  // Create a new Date object representing midnight 7 days ago
  const midnightSevenDaysAgo = new Date(year, month, day);

  return midnightSevenDaysAgo.toISOString();
}

